import React, {Component} from "react";
import './BlogPost.css';
import Post from "../../component/BlogPost/Post";
import '../../services/index';
import API from "../../services/index";

class BlogPost extends Component{
    state = {           
        listArticle: [], 
        insertArticle: {
            userId: 1,
            id: 1,
            title: "",
            body:""
        }
    }
    getDataFromServerAPI =() => {
        API.getNewsBlog().then(result => {
            this.setState({
                listArticle: result
            })
        })
    }
    componentDidMount(){
        this.getDataFromServerAPI()
    }

    handleSaveButton = () => {
        API.postNewsBlog(this.state.insertArticle)
        .then(()=>{
            this.getFromAPIServer();
        })
    }
    handleAddArticle = (event) => {
        let formInsertArticle = { ...this.state.insertArticle };
        let timestamp = new Date().getTime();
        formInsertArticle['id'] = timestamp;
        formInsertArticle[event.target.name] = event.target.value;
        this.setState({ insertArticle: formInsertArticle });
    }
    render(){
        return(
            <div className="post-artikel">
        <div className="form pb-2 border-bottom">
          <div className="form-group row">
            <label htmlFor="title" className="col-sm-2 col-form-label">
              Judul
            </label>
            <div className="col-sm-10">
              <input
                type="text"
                className="form-control"
                id="title"
                name="title"
                onChange={this.handleAddArticle}
              />
            </div>
          </div>
          <div className="form-group row">
            <label htmlFor="body" className="col-sm-2 col-form-label">
              Isi
            </label>
            <div className="col-sm-10">
              <textarea
                type="text"
                className="form-control"
                id="body"
                name="body"
                rows="3"
                onChange={this.handleAddArticle}
              />
            </div>
          </div>
          <button
            type="submit"
            className="btn btn-primary"
            onClick={this.handleSaveButton}
          >
            Simpan
          </button>
        </div>
        <h2>Daftar Artikel</h2>
        {this.state.listArticle.map((listArticle) => {
          // looping dan masukan untuk setiap data yang ada di listArtikel ke variabel artikel
          return (
            <Post
              key={article.id}
              judul={article.title}
              isi={article.body}
              idArtikel={article.id}
              hapusArtikel={this.handleDeleteArticle}
            />
          ); // mappingkan data json dari API sesuai kategorinya
        })}
      </div>
        )
    }
}
export default BlogPost;

