import React, {Component} from "react";
import './BlogPost.css';
import Post from "../../component/BlogPost/Post";
import '../../services/index';
import API from "../../services/index";

class BlogPost extends Component{
    state = {           
        listArticle: [], 
        insertArticle: {
            userId: 1,
            id: 1,
            title: "",
            body:""
        }
    }
    getDataFromServerAPI =() => {
        API.getNewsBlog().then(result => {
            this.setState({
                listArticle: result
            })
        })
    }
    componentDidMount(){
        this.getDataFromServerAPI()
    }

    handleSaveButton = () => {
        fetch('http://localhost:3001/posts', {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }, 
            body: JSON.stringify(this.state.insertArticle)
        })
            .then((response: Response) => {
                this.getDataFromServerAPI();
            });
    }
    render(){
        return(
            <div className="post-article">
                <h2>List of Article</h2>
                {
                    this.state.listArticle.map(article => {
                        return <Post key={article.id} title={article.title} content={article.body}/> 

                    })
                }
            </div>
        )
    }
}
export default BlogPost;

