import React, {Component} from "react";
import './BlogPost.css';
import Post from "../component/Post";
import '../services/index';
import API from "../services/index";

class BlogPost extends Component{
    state = {           
        listStudent: [], 
        insertStudent: {
            id: 1,
            NIM: 1,
            nama: "",
            alamat:"",
            hp:"",
            angkatan: 1,
            status: "",
        }
    }
    getDataFromServerAPI = () => {
        API.getNewsBlog().then((result) => {
            this.setState({
                listStudent: result,
            });
        });
    };
    componentDidMount(){
        this.getDataFromServerAPI();
    }

    handleSaveButton = () => {
        API.postNewsBlog(this.state.insertStudent)
        .then((response) => {
            this.getDataFromServerAPI();
        });
    };

    handleAdd = (event) => {
        let formInsertStudent = { ...this.state.insertStudent };
        let timestamp = new Date().getTime();
        formInsertStudent['id'] = timestamp;
        formInsertStudent[event.target.name] = event.target.value;
        this.setState({ insertStudent: formInsertStudent, });
    };

    handleDelete = (data) => {
        API.deleteNewsBlog(data).then((response) => {
                this.getDataFromServerAPI();
            });
    };
    render(){
        return(
            <div className="container">
                <div className="row">
                    <div className="col-sm-12">
                        <div className="post-student">
                            <h2 className="title text center">Input New Student</h2>
                            <div className="form-group row">
                                <label htmlFor="NIM" className="col-sm-2 col-form-label">NIM</label>
                                <div className="col-sm-6">
                                    <textarea className="form-control" id="title" name="title" rows="1" onChange={this.handleAddstudent}></textarea>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default BlogPost;
